var waitStaffApp=angular.module("waitstaff",["ngRoute","ngAnimate"]).config(["$routeProvider",function(t){t.when("/",{templateUrl:"home.html",controller:"HomeCtrl"}).when("/newMeal",{templateUrl:"newMeal.html",controller:"NewMealCtrl"}).when("/myEarnings",{templateUrl:"myEarnings.html",controller:"MyEarningsCtrl"})}]).run(function(t){t.total=0,t.subtotal=0,t.tip=0,t.tipTotal=0,t.mealCount=0,t.avgTipPerMeal=0});waitStaffApp.controller("NewMealCtrl",["$scope","$rootScope",function(t,e){t.resetDetails=function(){t.mealPrice="",t.taxRate="",t.tipPercentage=""},t.initCharges=function(){t.subtotal=0,t.taxRate=0,t.tipPercentage=0,t.tip=0},t.init=function(){t.resetDetails(),t.initCharges()},t.init(),e.submitDetails=function(){t.customerCharges(),e.tipTotal+=t.tip,e.mealCount++,e.avgTipPerMeal=t.tipTotal/t.mealCount},t.customerCharges=function(){t.subtotal=t.mealPrice*(1+t.taxRate/100),t.tip=t.mealPrice*(t.tipPercentage/100),t.total=t.subtotal+t.tip}}]).controller("MyEarningsCtrl",["$scope","$rootScope",function(t,e){t.initEarnings=function(){e.tipTotal=0,e.mealCount=0,e.avgTipPerMeal=0},t.resetEarnings=function(){e.tipTotal=0,e.mealCount=0,e.avgTipPerMeal=0}}]).controller("HomeCtrl",["$scope",function(t){}]);