var waitStaffApp=angular.module("waitstaff",[]);waitStaffApp.controller("waitStaffController",["$scope",function(t){t.resetDetails=function(){t.mealPrice="",t.taxRate="",t.tipPercentage=""},t.initCharges=function(){t.subtotal=0,t.taxRate=0,t.tipPercentage=0,t.tip=0,t.total=0},t.initEarnings=function(){t.tipTotal=0,t.mealCount=0,t.avgTipPerMeal=0},t.init=function(){t.resetDetails(),t.initCharges(),t.initEarnings()},t.init(),t.submitDetails=function(){t.customerCharges(),t.tipTotal+=t.tip,t.mealCount++,t.avgTipPerMeal=t.tipTotal/t.mealCount},t.customerCharges=function(){t.subtotal=t.mealPrice*(1+t.taxRate/100),t.tip=t.mealPrice*(t.tipPercentage/100),t.total=t.subtotal+t.tip}}]);